gtPath = '../deploy/gt';pdPath = '../deploy/pd';groundTruths = dir(gtPath);%isplay(groundTruths);predictions = dir(pdPath);%display(predictions);skip = 2; % first two are '.' and '..' so skip themiter = 191;numClasses = 2;unknown_class = 10;totalpoints = 0;cf = zeros(iter, numClasses, numClasses);globalacc = 0;for i = 1:iter   display(num2str(i));      % iterate through prediction images   pred = imread(strcat(pdPath, '/', predictions(i + skip).name));      % labeles are from 0 to 31, add 1 to let them in 1 to 32   pred = pred + 1;      % get corresponding annotation images   annot = imread(strcat(gtPath, '/', groundTruths(i + skip).name));      % labels increse by 1 too   annot = annot + 1;      % find every pixels that is unkonwn class (32)   pixels_ignore = annot == unknown_class;      pred(pixels_ignore) = 0;   annot(pixels_ignore) = 0;      totalpoints = totalpoints + sum(annot(:) > 0);      % global and class accuracy computation   for j = 1:numClasses      for k = 1:numClasses         c1 = annot == j;         c1p = pred == k;         index = c1 .* c1p;         cf(i, j, k) = cf(i, j, k) + sum(index(:));      endfor            c1  = annot == j;      c1p = pred == j;      index = c1 .* c1p;      globalacc = globalacc + sum(index(:));      endforendforcf = sum(cf,1);cf = squeeze(cf);% Compute confusion matrixconf = zeros(numClasses);for i = 1:numClasses    if i ~= unknown_class && sum(cf(i,:)) > 0        conf(i,:) = cf(i,:)/sum(cf(i,:));    endendglobalacc = sum(globalacc)/sum(totalpoints);% Compute intersection over union for each class and its meanintoverunion = zeros(numClasses,1);for i = 1:numClasses    if i ~= unknown_class   && sum(conf(i,:)) > 0        intoverunion(i) = (cf(i,i))/(sum(cf(i,:))+sum(cf(:,i))-cf(i,i));    endenddisplay([' Global acc = ' num2str(globalacc) ' Class average acc = ' num2str(sum(diag(conf))/(numClasses)) ' Mean Int over Union = ' num2str(sum(intoverunion)/(numClasses))]);